def encrypt(t):
    encrypted_text = ''
    count = 0
    en = randint(0, 1470)
    for i in (ord(i) for i in t):
        a = i
        if count % 2 == 0:
            i += dig1[count]+en
        else:
            i += dig2[count]+en
        encrypted_text += chr(key[i])
        count += 1
    return encrypted_text+chr(key[en]) 

def decrypt(t):
    decrypted_text = ''
    en_ = key.index(ord(t[-1]))
    count = 0
    for i in t[:-1]:
        i = key.index(ord(i))
        a = i
        if count % 2 ==0:
            i -= dig1[count]+en_
        else: i -= dig2[count]+en_
        count+=1
        decrypted_text += chr(i)
    return decrypted_text

#Here Comes the tkinter part
def print_output():
    inp = input_text.get(1.0, "end-2c")
    try:
        if ord(inp[0]) > 900:
            decrypto = decrypt(inp.strip())
            lbl.config(text = "DECRYPTED TEXT COPIED TO YOUR CLIPBOARD\n"+decrypto)
            copy(decrypto)
        else:
            encrypto= encrypt(inp.strip())
            lbl.config(text = "ENCRYPTED TEXT COPIED TO YOUR CLIPBOARD\n"+encrypto)
            copy(encrypto)
    except IndexError:
        pass

def enter(event):
    print_output()
    input_text.delete(index1='1.0', index2='end')

